import "next-auth";
import "next-auth/jwt";
import type { DefaultSession } from "next-auth";

declare module "next-auth" {
  interface Session {
    user: {
      id: string;
      slotLimit: number;
    } & DefaultSession["user"];
  }

  interface User {
    slotLimit: number;
  }
}

declare module "next-auth/jwt" {
  interface JWT {
    id?: string;
    slotLimit?: number;
  }
}
